mapper.select:
SELECT mid, password, name, email, gender, `Call`
FROM admin
WHERE mid = #{mid}
  AND password = #{password}

SELECT SQL_CALC_FOUND_ROWS uid, gender, birth, homeadd, presentadd, email, `call`, name, createdate, password,
postcode, bloodtype,logout
FROM User
WHERE


        uid LIKE CONCAT('%',#{uid},'%')


        uid LIKE '%%'



    AND gender=#{gender}


    AND birth=#{birth}


    AND homeadd LIKE CONCAT('%',#{homeadd},'%')


    AND presentadd LIKE CONCAT('%',#{presentadd},'%')


    AND email LIKE CONCAT('%',#{email},'%')


    AND call LIKE CONCAT('%',#{call},'%')


    AND name LIKE CONCAT('%',#{name},'%')


    AND postcode LIKE CONCAT('%',#{postcode},'%')


    AND bloodtype=#{bloodtype}



        ORDER BY #{order}


        ORDER BY _id




        LIMIT #{limit}


        LIMIT 10



    OFFSET #{offset}
;
SELECT FOUND_ROWS() as total;

mapper.update:
UPDATE User
uid=#{uid}, password=#{password}, gender=#{gender}, birth=#{birth}, homeadd=#{homeadd}, presentadd=#{presentadd}, email=#{email}, `call`=#{call}, `name`=#{name}, postcode=#{postcode}, bloodtype=#{bloodtype}
WHERE uid=#{uid}

UPDATE user
SET logout=#{logout}
WHERE uid = #{uid}
