mapper.select:
SELECT mid, password, name, email, gender, `Call`
FROM admin
WHERE mid = #{mid}
  AND password = #{password}

SELECT SQL_CALC_FOUND_ROWS uid, gender, birth, homeadd, presentadd, email, `call`, name, createdate, password,
postcode, bloodtype,logout
FROM User
WHERE


        uid LIKE CONCAT('%',#{uid},'%')


        uid LIKE '%%'



    AND gender=#{gender}


    AND birth=#{birth}


    AND homeadd LIKE CONCAT('%',#{homeadd},'%')


    AND presentadd LIKE CONCAT('%',#{presentadd},'%')


    AND email LIKE CONCAT('%',#{email},'%')


    AND call LIKE CONCAT('%',#{call},'%')


    AND name LIKE CONCAT('%',#{name},'%')


    AND postcode LIKE CONCAT('%',#{postcode},'%')


    AND bloodtype=#{bloodtype}



        ORDER BY #{order}


        ORDER BY uid




        LIMIT #{limit}


        LIMIT 10



    OFFSET #{offset}
;
SELECT FOUND_ROWS() as total;

mapper.update:
UPDATE User
uid=#{uid}, password=#{password}, gender=#{gender}, birth=#{birth}, homeadd=#{homeadd}, presentadd=#{presentadd}, email=#{email}, `call`=#{call}, `name`=#{name}, postcode=#{postcode}, bloodtype=#{bloodtype}
WHERE uid=#{uid}

UPDATE user
SET logout=#{logout}
WHERE uid = #{uid}

mapper.select:
SELECT SQL_CALC_FOUND_ROWS bkid, bkname, price, state, introduce, remark, publisher, storedate, isbn, isbn10, imgs, publishadd, publishdate, page, category, language, chinaclass, binding, atname, atid
FROM book_author
WHERE


        bkid LIKE CONCAT('%',#{bkid},'%')


        bkid LIKE '%%'




    AND state=#{state}


    AND bkname LIKE CONCAT('%',#{bkname},'%')


    AND atname LIKE CONCAT('%',#{atname},'%')


    AND isbn LIKE CONCAT('%',#{isbn},'%'),


    AND isbn10 LIKE CONCAT('%',#{isbn10},'%')


    AND language LIKE CONCAT('%',#{language},'%')


    AND binding LIKE CONCAT('%',#{binding},'%')


    AND chinaclass LIKE CONCAT('%',#{chinaclass},'%')



        ORDER BY #{order}


        ORDER BY bkid




        LIMIT #{limit}


        LIMIT 10



    OFFSET #{offset}
;
SELECT FOUND_ROWS() as total;

mapper.update:
UPDATE book
SET state='NOTINCLUDE'
WHERE bkid = #{bkid}

mapper.select:
SELECT SQL_CALC_FOUND_ROWS borrowid, uid, createtime, returntime, state, bkid, limittime, remark, uname, bkname, timeleft, time
FROM borrow_book_user
WHERE


        borrowid LIKE CONCAT('%',#{borrowid},'%')


        borrowid LIKE '%%'




    AND bkid LIKE CONCAT('%',#{bkid},'%')


    AND uid LIKE CONCAT('%',#{uid},'%')


    AND state=#{state}




        ORDER BY #{order}


        ORDER BY borrowid




        LIMIT #{limit}


        LIMIT 10



    OFFSET #{offset}
;
SELECT FOUND_ROWS() as total;
