mapper.select:
SELECT SQL_CALC_FOUND_ROWS bkname, bkid, atid, atname, state, remark, publisher, storedate, binding, language,
category
FROM Book_Author
WHERE


        bkname LIKE concat('%', #{bkname}, '%')


        bkname LIKE '%%'



    AND state = #{state}


    AND publisher LIKE concat('%', #{publisher}, '%')


    AND atname like concat('%', #{atname}, '%')



        ORDER BY #{order}


        ORDER BY bkname




        LIMIT #{limit}


        LIMIT 10



    OFFSET #{offset}
;
SELECT FOUND_ROWS() as total;

SELECT bkname, bkid, atid, atname, state, remark, publisher, storedate, binding, language,
category
FROM Book_Author


        ORDER BY #{order}


        ORDER BY bkname




        LIMIT #{limit}


        LIMIT 10



    OFFSET #{offset}

mapper.update:
UPDATE Book
name=#{name}, atid=#{atid}, price=#{price}, state=#{state}, introduce=#{introduce}, remark=#{remark}, publisher=#{publisher}
WHERE bkid=#{bkid}

UPDATE Book

    Bkid=#{bkid}
    storedate
    ISBN=#{ISBN}

WHERE bkid=#{old_bkid}
