mapper.select:
SELECT SQL_CALC_FOUND_ROWS borrowid, uid, createtime, returntime, state, bkid, mid, limittime, remark, bkname, timeleft, time
FROM borrow_book_user
WHERE
uid = #{uid}

    AND state=#{state}



        ORDER BY #{order}


        ORDER BY borrowid




        LIMIT #{limit}


        LIMIT 10



    OFFSET #{offset}
;
SELECT FOUND_ROWS() as total;

SELECT SQL_CALC_FOUND_ROWS borrowid, uid, createtime, returntime,mid,mname, state, bkid, limittime, remark, uname, bkname, timeleft, time
FROM borrow_book_user
WHERE


        borrowid LIKE CONCAT('%',#{borrowid},'%')


        borrowid LIKE '%%'




    AND bkid LIKE CONCAT('%',#{bkid},'%')


    AND uid LIKE CONCAT('%',#{uid},'%')


    AND state=#{state}




        ORDER BY #{order}


        ORDER BY borrowid




        LIMIT #{limit}


        LIMIT 10



    OFFSET #{offset}
;
SELECT FOUND_ROWS() as total;
