mapper.select:
SELECT uid, gender, birth, homeadd, presentadd, email, `call`, name, createdate, postcode, bloodtype,logout
FROM user
WHERE uid = #{uid}
  AND password = #{password}

SELECT uid, gender, birth, homeadd, presentadd, email, `call`, name, createdate, postcode, bloodtype
FROM user
WHERE uid = #{uid}

mapper.update:
UPDATE user
SET password=#{password}
WHERE uid = #{uid}
  AND password = #{oldPassword}

UPDATE User
gender=#{gender}, birth=#{birth}, homeadd=#{homeadd}, presentadd=#{presentadd}, email=#{email}, `call`=#{call}, `name`=#{name}, postcode=#{postcode}, bloodtype=#{bloodtype}
WHERE uid=#{uid}
